        </DialogContent>
      </Dialog>
    </>
  );
}

client/src/components/DeckViewerModal.ts.content:
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from '@/components/ui/dialog';
import { Card } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { type GameCardData } from './GameCard';
import { Skull, Sword, Shield, Heart, Sparkles, Zap, Scroll } from 'lucide-react';

interface DeckViewerModalProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  remainingCards: GameCardData[];
}

export default function DeckViewerModal({ open, onOpenChange, remainingCards }: DeckViewerModalProps) {
  const cardsByType = {
    monster: remainingCards.filter(c => c.type === 'monster'),
    weapon: remainingCards.filter(c => c.type === 'weapon'),
    shield: remainingCards.filter(c => c.type === 'shield'),
    potion: remainingCards.filter(c => c.type === 'potion'),
    amulet: remainingCards.filter(c => c.type === 'amulet'),
    skill: remainingCards.filter(c => c.type === 'skill'),
    event: remainingCards.filter(c => c.type === 'event'),
  };

  const getIcon = (type: string) => {
    switch (type) {
      case 'monster':
        return <Skull className="w-5 h-5 text-primary" />;
      case 'weapon':
        return <Sword className="w-5 h-5 text-amber-500" />;
      case 'shield':
        return <Shield className="w-5 h-5 text-blue-500" />;
      case 'potion':
        return <Heart className="w-5 h-5 text-destructive" />;
      case 'amulet':
        return <Sparkles className="w-5 h-5 text-purple-500" />;
      case 'skill':
        return <Zap className="w-5 h-5 text-cyan-500" />;
      case 'event':
        return <Scroll className="w-5 h-5 text-violet-500" />;
      default:
        return null;
    }
  };

  const getTypeLabel = (type: string) => {
    return type.charAt(0).toUpperCase() + type.slice(1) + 's';
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-3xl max-h-[80vh] overflow-y-auto" data-testid="deck-viewer-modal">
        <DialogHeader>
          <DialogTitle className="font-serif text-2xl">Remaining Deck</DialogTitle>
          <DialogDescription>
            {remainingCards.length} cards left in the deck
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-6 py-4">
          {Object.entries(cardsByType).map(([type, cards]) => (
            cards.length > 0 && (
              <div key={type} className="space-y-3">
                <div className="flex items-center gap-2">
                  {getIcon(type)}
                  <h3 className="font-semibold">{getTypeLabel(type)}</h3>
                  <Badge variant="outline" className="font-mono">
                    {cards.length}
                  </Badge>
                </div>

                <div className="grid grid-cols-6 gap-2">
                  {cards.map((card) => (
                    <Card
                      key={card.id}
                      className="p-2 flex flex-col items-center gap-1 bg-muted/50 hover-elevate"
                    >
                      {card.image && (
                        <div className="w-full aspect-square rounded overflow-hidden bg-gradient-to-b from-muted to-card">
                          <img
                            src={card.image}
                            alt={card.name}
                            className="w-full h-full object-cover"
                          />
                        </div>
                      )}
                      <div className="text-center w-full">
                        <p className="text-[10px] font-medium truncate">{card.name}</p>
                        <Badge variant="outline" className="text-xs">
                          {card.value}
                        </Badge>
                      </div>
                    </Card>
                  ))}
                </div>
              </div>
            )
          ))}

          {remainingCards.length === 0 && (
            <div className="text-center py-8 text-muted-foreground">
              <p>The deck is empty. Victory is near!</p>
            </div>
          )}
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/AmuletSlot.tsx.content:
import { Card } from '@/components/ui/card';
import { Sparkles, Heart, Sword, Shield } from 'lucide-react';
import { useEffect, useRef } from 'react';
import { initMobileDrop } from '../utils/mobileDragDrop';

interface AmuletSlotProps {
  amulet: { 
    name: string; 
    value: number; 
    image?: string; 
    type: 'amulet'; 
    effect: 'health' | 'attack' | 'defense';
  } | null;
  onDrop?: (card: any) => void;
  isDropTarget?: boolean;
}

export default function AmuletSlot({ amulet, onDrop, isDropTarget }: AmuletSlotProps) {
  const amuletRef = useRef<HTMLDivElement>(null);

  // Set up mobile drop support
  useEffect(() => {
    if (!amuletRef.current || !onDrop) return;

    const cleanup = initMobileDrop(
      amuletRef.current,
      (dragData) => {
        if (dragData.type === 'card' && dragData.data.type === 'amulet') {
          onDrop(dragData.data);
        }
      },
      ['card'] // Accept card drops
    );

    return cleanup;
  }, [onDrop]);

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    const cardData = e.dataTransfer.getData('card');
    if (cardData) {
      const card = JSON.parse(cardData);
      if (card.type === 'amulet') {
        onDrop?.(card);
      }
    }
  };

  const getEffectIcon = () => {
    if (!amulet) return <Sparkles className="w-6 h-6 sm:w-8 sm:h-8 text-muted-foreground" />;
    switch (amulet.effect) {
      case 'health':
        return <Heart className="w-4 h-4 sm:w-5 sm:h-5 text-green-500" />;
      case 'attack':
        return <Sword className="w-4 h-4 sm:w-5 sm:h-5 text-red-500" />;
      case 'defense':
        return <Shield className="w-4 h-4 sm:w-5 sm:h-5 text-blue-500" />;
    }
  };

  const getEffectLabel = () => {
    if (!amulet) return null;
    switch (amulet.effect) {
      case 'health':
        return `+${amulet.value} Max HP`;
      case 'attack':
        return `+${amulet.value} ATK`;
      case 'defense':
        return `+${amulet.value} DEF`;
    }
  };

  return (
    <div 
      ref={amuletRef}
      onDragOver={handleDragOver}
      onDrop={handleDrop}
      style={{ 
        width: 'clamp(80px, 12vw, 160px)', 
        height: 'clamp(112px, 16.8vw, 224px)' 
      }}
      data-testid="slot-amulet"
    >
      <Card className={`
        w-full h-full border-4 border-dashed overflow-hidden
        transition-all duration-200
        ${amulet ? 'border-purple-900 bg-purple-950/20' : 'border-muted bg-muted/20'}
        ${isDropTarget ? 'scale-105 border-primary animate-pulse' : ''}
      `}>
        <div className="h-full flex flex-col items-center justify-center p-2">
          {amulet ? (
            <>
              {amulet.image && (
                <img 
                  src={amulet.image} 
                  alt={amulet.name}
                  className="w-full h-[60%] object-contain mb-2"
                />
              )}
              <div className="flex items-center gap-1 bg-background/90 backdrop-blur-sm rounded px-2 py-1">
                {getEffectIcon()}
                <span className="text-xs sm:text-sm font-bold">{getEffectLabel()}</span>
              </div>
              <span className="text-[8px] sm:text-xs text-center mt-1 text-muted-foreground line-clamp-1">
                {amulet.name}
              </span>
            </>
          ) : (
            <>
              <Sparkles className="w-8 h-8 sm:w-12 sm:h-12 md:w-16 md:h-16 text-muted-foreground mb-2" />
              <span className="text-[8px] sm:text-xs md:text-sm text-muted-foreground font-semibold">
                Amulet
              </span>
            </>
          )}
        </div>
      </Card>
    </div>
  );
}

