import { Card } from '@/components/ui/card';
import { Hand } from 'lucide-react';
import GameCard, { type GameCardData } from './GameCard';

interface HandAreaProps {
  handCards: GameCardData[];
  onDropToHand?: (card: GameCardData) => void;
  onDragCardFromHand?: (card: GameCardData) => void;
  onDragEndFromHand?: () => void;
  isDropTarget?: boolean;
  maxHandSize?: number;
}

export default function HandArea({ 
  handCards, 
  onDropToHand, 
  onDragCardFromHand,
  onDragEndFromHand,
  isDropTarget,
  maxHandSize = 5 
}: HandAreaProps) {
  const handleDragOver = (e: React.DragEvent) => {
    if (handCards.length < maxHandSize) {
      e.preventDefault();
    }
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    const cardData = e.dataTransfer.getData('card');
    if (cardData && handCards.length < maxHandSize) {
      const card = JSON.parse(cardData);
      onDropToHand?.(card);
    }
  };

  return (
    <div 
      className="fixed bottom-0 left-0 right-0 bg-card/95 backdrop-blur-sm border-t-2 border-card-border shadow-2xl"
      style={{ height: 'clamp(140px, 18vh, 240px)' }}
    >
      <div className="h-full px-4 py-2 lg:px-8">
        {/* Hand header */}
        <div className="flex items-center justify-between mb-2">
          <div className="flex items-center gap-2">
            <Hand className="w-5 h-5 text-primary" />
            <span className="font-bold text-lg">Hand</span>
            <span className="text-muted-foreground">({handCards.length}/{maxHandSize})</span>
          </div>
          {handCards.length === 0 && (
            <p className="text-sm text-muted-foreground">Drag cards here to save them for later</p>
          )}
        </div>

        {/* Hand cards area */}
        <div 
          onDragOver={handleDragOver}
          onDrop={handleDrop}
          className={`
            h-[calc(100%-2rem)] rounded-lg border-2 transition-all duration-200
            ${isDropTarget && handCards.length < maxHandSize 
              ? 'border-primary border-dashed bg-primary/10' 
              : 'border-transparent'
            }
          `}
        >
          {handCards.length > 0 ? (
            <div className="flex gap-3 p-2 h-full items-center overflow-x-auto">
              {handCards.map((card) => (
                <div key={card.id} style={{ flexShrink: 0 }}>
                  <GameCard
                    card={card}
                    onDragStart={onDragCardFromHand}
                    onDragEnd={onDragEndFromHand}
                  />
                </div>
              ))}
            </div>
          ) : (
            <div className="h-full flex items-center justify-center">
              <div className="text-center">
                <Hand className="w-12 h-12 text-muted-foreground mx-auto mb-2 opacity-50" />
                <p className="text-sm text-muted-foreground">
                  {handCards.length < maxHandSize 
                    ? "Your hand is empty - drag cards here to save them" 
                    : "Hand is full (5 card limit)"
                  }
                </p>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

client/src/components/EventChoiceModal.t.content:
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Calendar } from 'lucide-react';
import { type GameCardData } from './GameCard';

interface EventChoiceModalProps {
  open: boolean;
  eventCard: GameCardData | null;
  onChoice: (choiceIndex: number) => void;
}

export default function EventChoiceModal({ open, eventCard, onChoice }: EventChoiceModalProps) {
  if (!eventCard || !eventCard.eventChoices) return null;

  return (
    <Dialog open={open}>
      <DialogContent className="sm:max-w-md">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <Calendar className="w-5 h-5 text-pink-500" />
            {eventCard.name}
          </DialogTitle>
          <DialogDescription>
            Choose your path wisely...
          </DialogDescription>
        </DialogHeader>

        <div className="flex flex-col gap-3 mt-4">
          {eventCard.eventChoices.map((choice, index) => (
            <Button
              key={index}
              variant="outline"
              className="w-full text-left justify-start p-4 h-auto"
              onClick={() => onChoice(index)}
            >
              <div className="flex flex-col items-start">
                <span className="font-semibold">{choice.text}</span>
                {choice.effect !== 'none' && (
                  <span className="text-xs text-muted-foreground mt-1">
                    {formatEffect(choice.effect)}
                  </span>
                )}
              </div>
            </Button>
          ))}
        </div>
      </DialogContent>
    </Dialog>
  );
}

function formatEffect(effect: string): string {
  const parts = effect.split(',');
  return parts.map(part => {
    if (part.includes('hp-')) return `Lose ${part.replace('hp-', '')} HP`;
    if (part.includes('hp+')) return `Gain ${part.replace('hp+', '')} HP`;
    if (part.includes('heal+')) return `Heal ${part.replace('heal+', '')} HP`;
    if (part.includes('gold-')) return `Lose ${part.replace('gold-', '')} Gold`;
    if (part.includes('gold+')) return `Gain ${part.replace('gold+', '')} Gold`;
    if (part.includes('maxhp+')) return `+${part.replace('maxhp+', '')} Max HP`;
    if (part.includes('fullheal')) return 'Full Heal';
    if (part.includes('weapon')) return 'Get Random Weapon';
    if (part.includes('permanentskill')) return 'Gain Random Permanent Skill';
    return part;
  }).join(', ');
}

client/src/components/HandDisplay.tsx.content:
import { useState, useRef, useEffect } from 'react';
import GameCard, { type GameCardData } from './GameCard';

interface HandDisplayProps {
  handCards: GameCardData[];
  onPlayCard?: (card: GameCardData, target?: any) => void;
  onDragCardFromHand?: (card: GameCardData) => void;
  onDragEndFromHand?: () => void;
  maxHandSize?: number;
}

export default function HandDisplay({ 
  handCards, 
  onPlayCard,
  onDragCardFromHand,
  onDragEndFromHand,
  maxHandSize = 7
}: HandDisplayProps) {
  const [hoveredIndex, setHoveredIndex] = useState<number | null>(null);
  const [isDraggingCard, setIsDraggingCard] = useState(false);
  const containerRef = useRef<HTMLDivElement>(null);

  // Calculate fan arrangement
  const calculateCardTransform = (index: number, total: number) => {
    if (total === 0) return { rotate: 0, translateX: 0, translateY: 0 };

    const centerIndex = (total - 1) / 2;
    const offset = index - centerIndex;

    // Calculate rotation - spread cards in an arc
    const maxRotation = Math.min(20, total * 3); // Max 20 degrees, or 3 degrees per card
    const rotationStep = maxRotation / Math.max(1, total - 1);
    const rotation = offset * rotationStep;

    // Calculate horizontal spread
    const cardWidth = 120; // Approximate card width in pixels
    const overlap = 0.3; // 30% overlap
    const horizontalSpacing = cardWidth * (1 - overlap);
    const translateX = offset * horizontalSpacing;

    // Calculate vertical position (cards at edges slightly lower)
    const translateY = Math.abs(offset) * Math.abs(offset) * 2;

    return {
      rotate: rotation,
      translateX: translateX,
      translateY: translateY
    };
  };

  const handleDragStart = (card: GameCardData) => {
    setIsDraggingCard(true);
    onDragCardFromHand?.(card);
  };

  const handleDragEnd = () => {
    setIsDraggingCard(false);
    onDragEndFromHand?.();
  };


  return (
    <>
      {/* Main Hand Display - Lower position with overflow */}
      <div 
        className="fixed left-0 right-0 pointer-events-none overflow-hidden"
        style={{ 
          bottom: '0px', 
          height: '140px', 
          zIndex: 20,
          paddingBottom: '20px'
        }}
      >
        <div className="relative h-full flex items-end justify-center" style={{ paddingBottom: '50px' }}>
          {/* Cards container with perspective */}
          <div 
            ref={containerRef}
            className="relative"
            style={{ 
              perspective: '1000px',
              transformStyle: 'preserve-3d'
            }}
          >
            {handCards.map((card, index) => {
              const transform = calculateCardTransform(index, handCards.length);
              const isHovered = hoveredIndex === index;

              return (
                <div
                  key={card.id}
                  className={`
                    absolute pointer-events-auto
                    transition-all duration-200 ease-out
                  `}
                  style={{
                    transform: `
                      translateX(${transform.translateX}px)
                      translateY(${isHovered ? -80 : transform.translateY}px)
                      rotate(${transform.rotate}deg)
                      scale(${isHovered ? 1.1 : 1})
                    `,
                    zIndex: isHovered ? 100 : index + 1,
                    transformOrigin: 'bottom center',
                    filter: isHovered ? 'brightness(1.1)' : 'none',
                  }}
                  onMouseEnter={() => !isDraggingCard && setHoveredIndex(index)}
                  onMouseLeave={() => setHoveredIndex(null)}
                  data-testid={`hand-card-${index}`}
                >
                  <GameCard
                    card={card}
                    onDragStart={handleDragStart}
                    onDragEnd={handleDragEnd}
                    className={isHovered ? 'shadow-2xl' : 'shadow-lg'}
                  />
                </div>
              );
            })}

          </div>
        </div>
      </div>

    </>
  );
}

